    },
  });
  return (
    <div>
      <p>
        <label>Email</label>
        <input
          type="text"
          id="emailField"
          onChange={formik.handleChange}
          value={formik.values.emailField}
        />
        <label>Password</label>
        <input
          type="text"
          id="pswField"
          onChange={formik.handleChange}
          value={formik.values.pswField}
        />
        <button id="submitBtn">submit</button>
      </p>
    </div>
  );
}

export default App;
